import React from "react";
import { useState } from "react";
import "./index.css";

export default function App() {
  return <Counter />;
}

function Counter() {
  let [step, setStep] = useState(1);
  let [count, setCount] = useState(0);
  return (
    <div className="counter">
      <div className="step">
        <button>-</button>
        <div>Step: {step}</div>
        <button>+</button>
      </div>
      <div className="count">
        <button>-</button>
        <div>Count: {count}</div>
        <button>+</button>
      </div>
      Count
    </div>
  );
}
