import { useState } from "react";
import "./index.css";

function App() {
  return (
    <div className="App">
      <BillPrice />
      <ServiceGrade> How did you like the service </ServiceGrade>
      <ServiceGrade> How did your friend like the service? </ServiceGrade>
      <PayTotal />
      <Reset />
    </div>
  );
}

function BillPrice() {
  const [bill, setBill] = useState(0);
  return (
    <div>
      <label>How much was the bill?</label>
      <input type="text" value={bill}></input>
    </div>
  );
}

function ServiceGrade({ children }) {
  return (
    <div>
      <label>{children}</label>
      <select>
        <option>Dissatisfied (0%)</option>
        <option>It was okay (5%)</option>
        <option>It was good (10%)</option>
        <option>Absolutely amazing (20%)</option>
      </select>
    </div>
  );
}

function PayTotal() {
  return <h2>You pay $100</h2>;
}

function Reset() {
  return <button>Reset</button>;
}

export default App;
