import { useState } from "react";
import "./index.css";

function App() {
  const [bill, setBill] = useState(0);
  const [percentage1, setPercentage1] = useState(0);
  const [percentage2, setPercentage2] = useState(0);

  return (
    <div className="App">
      <BillPrice bill={bill} setBill={setBill} />
      <ServiceTip onChange={setPercentage1}>
        How did you like the service{" "}
      </ServiceTip>
      <ServiceTip onChange={setPercentage2}>
        {" "}
        How did your friend like the service?{" "}
      </ServiceTip>
      <PayTotal bill={bill} />
      <Reset />
    </div>
  );
}

function BillPrice({ bill, setBill }) {
  return (
    <div>
      <label>How much was the bill?</label>
      <input
        type="text"
        value={bill}
        onChange={(e) => setBill(e.target.value)}
      ></input>
    </div>
  );
}

function ServiceTip({ setPercentage, children }) {
  return (
    <div>
      <label>{children}</label>
      <select onChange={(e) => setPercentage(e.target.value)}>
        <option value={0}>Dissatisfied (0%)</option>
        <option value={5}>It was okay (5%)</option>
        <option value={10}>It was good (10%)</option>
        <option value={20}>Absolutely amazing (20%)</option>
      </select>
    </div>
  );
}

function PayTotal({ bill, tip }) {
  return (
    <h2>
      You pay ${Number(bill) + Number(tip)} (${bill} + $
      {tip.reduce((curSum, a) => curSum + a, 0)})
    </h2>
  );
}

function Reset() {
  return <button>Reset</button>;
}

export default App;
