import { useState } from "react";
import "./index.css";

function App() {
  return (
    <div className="App">
      <BillPrice />
      <ServiceTip> How did you like the service </ServiceTip>
      <ServiceTip> How did your friend like the service? </ServiceTip>
      <PayTotal />
      <Reset />
    </div>
  );
}

function BillPrice() {
  const [bill, setBill] = useState(0);
  return (
    <div>
      <label>How much was the bill?</label>
      <input
        type="text"
        value={bill}
        onChange={(e) => setBill(e.target.value)}
      ></input>
    </div>
  );
}

function ServiceTip({ children }) {
  const [tip, setTip] = useState(0);
  return (
    <div>
      <label>{children}</label>
      <select>
        <option value={0}>Dissatisfied (0%)</option>
        <option>It was okay (5%)</option>
        <option>It was good (10%)</option>
        <option>Absolutely amazing (20%)</option>
      </select>
    </div>
  );
}

function PayTotal() {
  return <h2>You pay $100</h2>;
}

function Reset() {
  return <button>Reset</button>;
}

export default App;
