import { useEffect, useState } from "react";
import "./index.css";

// `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

export default function App() {
  const [amount, setAmount] = useState(1);
  const [convertedAmount, setConvertedAmount] = useState(1);
  const [convertFrom, setConvertFrom] = useState("USD");
  const [convertTo, setConvertTo] = useState("EUR");

  useEffect(
    function () {
      async function fetchConversion() {
        if (!amount) {
          setConvertedAmount(0);
          return;
        }
        const res = await fetch(
          `https://api.frankfurter.app/latest?amount=${amount}&from=${convertFrom}&to=${convertTo}`
        );
        const data = await res.json();
        setConvertedAmount(data.rates[convertTo]);
      }
      fetchConversion();
    },
    [amount, convertFrom, convertTo]
  );

  return (
    <div>
      <InputField value={amount} onChange={setAmount} />
      <SelectCurrency onSelect={setConvertFrom} value={convertFrom} />
      <SelectCurrency onSelect={setConvertTo} value={convertTo} />
      <p>{convertedAmount}</p>
    </div>
  );
}

function InputField({ value, onChange }) {
  return (
    <input
      type="text"
      value={value}
      onChange={(e) => onChange(e.target.value)}
    />
  );
}

function SelectCurrency({ onSelect, value }) {
  return (
    <select onChange={(e) => onSelect(e.target.value)} value={value}>
      <option value="USD">USD</option>
      <option value="EUR">EUR</option>
      <option value="CAD">CAD</option>
      <option value="INR">INR</option>
    </select>
  );
}
