import { useState } from "react";
import data from "./data.js";
import "./styles.css";

function App() {
  const [multiselection, setMultiselection] = useState(false);

  function handleClickMultiselection() {
    setMultiselection(!multiselection);
  }

  return (
    <div className="acc-wrapper">
      <Button onClick={handleClickMultiselection}>
        {multiselection ? "Disable" : "Enable"} multiselection
      </Button>
      <div className="accordion">
        <Accordion multiselection={multiselection} />
      </div>
    </div>
  );
}

function Accordion({ multiselection }) {
  const [curOpen, setCurOpen] = useState(null);

  return (
    <div>
      {data.map((question) => (
        <AccordionItem
          questionObj={question}
          key={question.id}
          multiselection={multiselection}
          curOpen={curOpen}
          setCurOpen={setCurOpen}
        />
      ))}
    </div>
  );
}

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function AccordionItem({ questionObj, curOpen, setCurOpen, multiselection }) {
  const [isOpen, setIsOpen] = useState(false);

  function handleOpen() {
    if (questionObj.id !== curOpen.id) {
      setCurOpen(questionObj) && setIsOpen(true);
    }
  }

  return (
    <>
      <div className="item">
        <div className="written-content">
          <h3 className="title">{questionObj.question}</h3>
          {isOpen && <p className="acc-content">{questionObj.answer}</p>}
        </div>
        <span onClick={handleOpen}>{isOpen ? "-" : "+"}</span>
      </div>
    </>
  );
}

export default App;
