import data from "./data.js";
import "./styles.css";

function App() {
  return (
    <div className="acc-wrapper">
      <div class="accordion">
        <Accordion />
      </div>
    </div>
  );
}

function Accordion() {
  return (
    <div>
      {data.map((question) => (
        <AccordionItem questionObj={question} />
      ))}
    </div>
  );
}

function AccordionItem({ questionObj }) {
  return (
    <div className="item">
      <h3 className="title">{questionObj.question}</h3>
      <p>{questionObj.answer}</p>
    </div>
  );
}

export default App;
