import { useState } from "react";
import data from "./data.js";
import "./styles.css";

function App() {
  const [multiselection, setMultiselection] = useState(true);
  const [curOpen, setCurOpen] = useState(null);

  function handleClickMultiselection() {
    setMultiselection(!multiselection);
    setCurOpen(null);
  }

  return (
    <div className="acc-wrapper">
      <Button onClick={handleClickMultiselection}>
        {multiselection ? "Disable" : "Enable"} multiselection
      </Button>
      <div className="accordion">
        <Accordion
          multiselection={multiselection}
          setCurOpen={setCurOpen}
          curOpen={curOpen}
        />
      </div>
    </div>
  );
}

function Accordion({ multiselection, setCurOpen, curOpen }) {
  return (
    <div>
      {data.map((question) => (
        <AccordionItem
          questionObj={question}
          key={question.id}
          multiselection={multiselection}
          curOpen={curOpen}
          setCurOpen={setCurOpen}
        />
      ))}
    </div>
  );
}

function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function AccordionItem({ questionObj, multiselection, curOpen, setCurOpen }) {
  const [isOpen, setIsOpen] = useState(false);

  function handleOpen() {
    if (multiselection) {
      setIsOpen(!isOpen);
      setCurOpen(questionObj);
      return;
    }
  }

  return (
    <>
      <div className="item" onClick={handleOpen}>
        <div className="written-content">
          <h3 className="title">{questionObj.question}</h3>
          {isOpen && (curOpen ? curOpen.id === questionObj.id : false) && (
            <p className="acc-content">{questionObj.answer}</p>
          )}
        </div>
        <span>{isOpen && curOpen.id === questionObj.id ? "-" : "+"}</span>
      </div>
    </>
  );
}

export default App;
