import data from "./data.js";
import "./styles.css";

function App() {
  return (
    <div className="acc-wrapper">
      <Accordion className="accordion" />
    </div>
  );
}

function Accordion() {
  return (
    <div>
      {data.map((question) => (
        <AccordionItem questionObj={question} className="item" />
      ))}
    </div>
  );
}

function AccordionItem({ questionObj }) {
  return (
    <div>
      <h2>{questionObj.question}</h2>
      <p>{questionObj.answer}</p>
    </div>
  );
}

export default App;
