import { useState } from "react";
import data from "./data.js";
import "./styles.css";

function App() {
  return (
    <div className="acc-wrapper">
      <div class="accordion">
        <Accordion />
      </div>
    </div>
  );
}

function Accordion() {
  return (
    <div>
      <Button />
      {data.map((question) => (
        <AccordionItem questionObj={question} />
      ))}
    </div>
  );
}

function Button({ children }) {
  return <button className="button">{children}</button>;
}

function AccordionItem({ questionObj }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <div className="item">
        <div className="written-content">
          <h3 className="title">{questionObj.question}</h3>
          {isOpen && <p className="acc-content">{questionObj.answer}</p>}
        </div>
        <span onClick={() => setIsOpen(!isOpen)}>{isOpen ? "-" : "+"}</span>
      </div>
    </>
  );
}

export default App;
